(function(window,$){"use strict";var title=$("#id_title");var url=$("#id_url");var preview=$(".content-url .preview");var prefix=preview.data("prefix");title.on("input",function(){title.off("keyup");url.val(slugify(title.val())).trigger("change")});url.on("change",previewPath);url.on("input",previewPath);function slugify(s){s=s.toLowerCase().replace(/[\s\\/.,+*"'()\[\]{}:;?!~`@#%$^&_=]/g,"-").replace(/-+/g,"-");return encodeURIComponent($.trim(s))}function previewPath(){var absUrl=prefix+url.val();preview.text(absUrl)}previewPath()})(this,this.jQuery);